import React from "react";

const Sell = () => {
  return (  <div>
    <div className="prev-next">
      <div className="left">
        <Link to="/">Home</Link> /<span id="product-name">{data.name}</span>
      </div>
    </div>
    <div className="main-content">
      <div className="product-image">
        <img src={data.image} id="display-image" alt="DSLR image"></img>
      </div>
      <div className="product-buy">
        <h1>{data.name}</h1>
        <span id="product-id">ID: {data._id} </span>
        <h2 id="price-display"> Minimum Bid &#8377;{data.price}</h2>
        <input
          type="text"
          id="input-cart"
          placeholder="     Enter BID"
          onChange={typebid}
        ></input>
        <button
          type="button"
          id="btn-cart"
          onClick={() => {
            if (inputBid > data.price) {
              addToCart(data);
              console.log(currentCart);
            } else {
              notify_log();
            }
          }}
          style={{ cursor: "pointer" }}
        >
          Place BID
        </button>
        <div className="product-info">
          <h3>Product info</h3>
          <p>{data.description}</p>
        </div>
        <hr></hr>
      </div>
    </div>
    <Toaster />
  </div>
);
};

export default Sell;
