import React ,  { useState }from "react";
import "./recommender.css";
import { Link } from "react-router-dom";
import axios from "axios";



function Recommender() {
  const [selectedBodyType, setSelectedBodyType] = useState('');

  const handleSelectBodyType = async (bodyType) => {
    setSelectedBodyType(bodyType);
    try {
      const response = await fetch('http://localhost:5000/set_body_type', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ body_type: bodyType  }),
      });
      const data = await response.json();
      setClassificationResult(data.result);
    } catch (error) {
      console.error('Error getting body type:', error);
    }
  };

  return (
    <div className="whitebox">
   
      <h2>Select your body type</h2>
      <p>Unsure of your body type? 
      <span>  <Link to="/calculator"> <a>Use our calculator!</a> </Link> </span> </p>
      <div className="grid-container">
        <div className="grid-item">    <Link to="/triangle"> <img  className="hourglass" src={require("../images/hourglass.png")}  alt="hourglass" onClick={() => handleSelectBodyType('hourglass')} /> </Link></div>
        <div className="grid-item">  <Link to="/apple"> <img  className="apple"src={require("../images/apple.png")} alt="Body Type 2" onClick={() => handleSelectBodyType('apple')}/>  </Link></div>
        <div className="grid-item">  <Link to="/triangle"> <img  className="rectl" src={require("../images/rectangle.png")} alt="Body Type 3" onClick={() => handleSelectBodyType('rectangle')}/>  </Link></div>
        <div className="grid-item">  <Link to="/triangle"> <img  className="pear" src={require("../images/pear.png")} alt="Body Type 3"  onClick={() => handleSelectBodyType('triangle')}/>  </Link></div>
        <div className="grid-item">  <Link to="triangle"> <img  className="inverti" src={require("../images/inverti.png")} alt="Body Type 4" onClick={() => handleSelectBodyType('inverti')} /> </Link></div>
        {/* <div className="grid-item">  <Link to="/"> <img  className="rectangle"src={require("../images/rect.png")} alt="Body Type 5" /></Link></div> */}
      </div>
      {/* <button className="continue-button">CONTINUE</button> */}
    </div>
  );
}

export default Recommender;
