import React, { useState } from 'react';
import './triangle.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';

// Placeholder images, replace these with your own image URLs
const images = [
//   '../images/pear_image2.jpeg',
  'https://via.placeholder.com/300.png/07f/fff',
  'https://via.placeholder.com/300.png/05f/fff',
];

function Triangle() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="triangle-container">
      <h2>Select your body type</h2>
      <div className="triangle-image-container">
        <img src={images[currentImageIndex]} alt="Body type" />
      </div>
      <div className="triangle-button-container">
        <button className="triangle-yes-button">
          <FontAwesomeIcon icon={faHeart} /> Yes
        </button>
        <button className="triangle-next-button" onClick={handleNext}>Next</button>
      </div>
    </div>
  );
}

export default Triangle;
